import React from 'react'
import { useSelector } from 'react-redux'

import { timeConverter } from '../../utils/timeConverter'


import styles from './TaskItem.module.css'

export const TaskItem = ({ id, title, descr, selectedStatus }) => {


  const tasks = useSelector(state => state.createTask.tasks)

  console.log(tasks)

  return (
    <div className={styles.task}>

      <div className={`${styles.taskItemStatus} ${selectedStatus.toLocaleLowerCase()}-status`}>{selectedStatus}</div>

      <h2 className={styles.taskTitle}>{title}</h2>

      <span>{timeConverter(id)}</span>

    </div>
  )
}
