import React from 'react'
import { useSelector } from 'react-redux'

import styles from './StatusStatistics.module.css'

import activeImg from './img/activeImg.png'
import inprogressImg from './img/inprogress.png'
import urgentImg from './img/urgent.png'
import doneImg from './img/done.png'

export const StatusStatistics = () => {
  const statusCount = useSelector((state) => state.createTask.statusCount)
  const statusInfo = useSelector((state) => state.createTask.statusInfo)

  return (
    <div className={styles.StatusStatisticsWrap}>
      {statusInfo.map((obj) => (
        <div className={styles.StatusStatisticsItem}>
          <img src={`./img${obj.urlImg}`} alt={obj.name} />
          <div>
            <span>{obj.name}</span>
            <span>{statusCount[obj.name]}</span>
          </div>
        </div>
      ))}
    </div>
  )
}
