import { createSlice } from "@reduxjs/toolkit"


const initialState = {
  tasks: [
    {id: '12', title: 'test'}
  ]
}

export const tasksSlice = createSlice({
  name: 'tasks',
  initialState,
  reducers: {
    creaTask: (state, action) => {
      state.tasks = action.payload
    }
  }
})


export const { creaTask } = tasksSlice.actions

export default tasksSlice.reducer
