import React from 'react'
import { useSelector } from 'react-redux'

import styles from './Statistic.module.css'

import activeImg from './img/active.png'
import inprogressImg from './img/inprogress.png'
import urgentImg from './img/urgent.png'
import doneImg from './img/done.png'

export const Statistic = () => {
  const statusCount = useSelector((state) => state.createTask.statusCount)
  const statusInfo = useSelector((state) => state.createTask.statusInfo)

  const statisticsImg = [activeImg, inprogressImg, urgentImg, doneImg]

  return (
    <div className={styles.StatisticWrap}>
      {statusInfo.map((obj, index) => (
        <div className={styles.StatisticItem}>
          <img src={statisticsImg[index]} alt={obj.name} />
          <div className={styles.Statistic}>
            <span className={styles.StatisticCount}>{statusCount[obj.name]}</span>
            <span className={styles.StatisticText}>{obj.name}</span>
          </div>
        </div>
      ))}
    </div>
  )
}
