import { createSlice } from '@reduxjs/toolkit'


const initialState = {
  tasks: [
    {
      id: 12,
      title: 'Driver monitor and method for monitoring driver',
      descr: 'fdgfgfg',
      selectedStatus: '',
    },

    {
      id: 13,
      title: 'Encapsulated Fragile Insulation Materials',
      descr: 'fdgfgfg',
      selectedStatus: '',
    },
  ],

  statusInfo: [
    {name: 'active', urlImg: '/active', count: 2},
    {name: 'in progress', urlImg: '/inprogress', count: 5},
    {name: 'urgent', urlImg: '/urgent', count: 6},
    {name: 'done', urlImg: '/done', count: 2},
  ],
}

export const tasksSlice = createSlice({
  name: 'tasks',
  initialState,
  reducers: {
    creaTask: (state, action) => {
      state.tasks = action.payload
    },
    setSelectedStatus: (state, action) => {
      state.tasks.selectedStatus = action.payload
    },
  },
})


export const { creaTask, setSelectedStatus } = tasksSlice.actions

export default tasksSlice.reducer
