import { createSlice } from "@reduxjs/toolkit"


const initialState = {
  isOpen: false
}

export const openSlice = createSlice({
  name: 'isOpen',
  initialState,
  reducers: {
    setIsOpen: (state, action) => {
      state.isOpen = action.payload
    }
  }
})

export const { setIsOpen } = openSlice.actions

export default openSlice.reducer
