import React from 'react'
import { useDispatch, useSelector } from 'react-redux'

import { MyButton } from '../UI/button/MyButton'
import { setSelectedStatus } from '../../redux/slices/tasksSlice'

import styles from './Statuses.module.css'


const statusesArr = ['active', 'in progress', 'urgent', 'done']

export const Statuses = () => {

  const dispatch = useDispatch()
  // const selectedStatus = useSelector(state => state.createTask.selectedStatus)

  const [statusType, setStatusType] = React.useState('')

  React.useEffect(() => {
    dispatch(setSelectedStatus(statusType))
  }, [statusType])

  return (
    <div className={styles.statusesWrap}>

      {
        statusesArr
        .filter(status => status !== 'done')
        .map((status, index) => 
          <MyButton
            key={status + index}
            onClick={() => setStatusType(status.replace(/ /g,''))}
            className={statusType === status ? `${status.replace(/ /g,'')}Btn btn-vis` : `${status.replace(/ /g,'')}Btn`}
          >
            {status}
          </MyButton>
        )
      }
    </div>
  )
}
