import React from 'react'

import { MyButton } from '../UI/button/MyButton'

import styles from './Statuses.module.css'


const statusesArr = ['active', 'in progress', 'urgent', 'done']

export const Statuses = () => {

  const dispatch = useDispatch()
  const tasks = useSelector(state => state.createTask.tasks)

  const [statusType, setStatusType] = React.useState('')

  return (
    <div className={styles.statusesWrap}>

      {
        statusesArr
        .filter(status => status !== 'done')
        .map((status, index) => 
          <MyButton
            key={status + index}
            onClick={() => setStatusType(status)}
            className={statusType === status ? `${status.replace(/ /g,'')}Btn btn-vis` : `${status.replace(/ /g,'')}Btn`}
          >
            {status}
          </MyButton>
        )
      }
    </div>
  )
}
