import React from 'react'

import { useDispatch, useSelector } from 'react-redux'

import { MyButton } from '../UI/button/MyButton'
import { MyInput } from '../UI/input/MyInput'

import { creaTask } from '../../redux/slices/tasksSlice'
import { setIsOpen } from '../../redux/slices/isOpenSlice'
import { Textarea } from '../UI/textarea'

import styles from './CreateTask.module.css'

export const CreateTask = () => {
  const [title, setTitle] = React.useState('')
  const [descr, setDescr] = React.useState('')

  const dispatch = useDispatch()
  const tasks = useSelector(state => state.createTask.tasks)


  const createTask = () => {
    const newTask = {
      id: Date.now(),
      title,
      descr,
    }

    dispatch(creaTask([...tasks, newTask]))
    dispatch(setIsOpen(false))
    setTitle('')
  }

  return (
    <div>
      <div className={styles.CreateTaskWrap}>
        <h2>Create your own Task</h2>

        <div className={styles.CreateTaskItem}>
          <MyInput
            onChange={e => setTitle(e.target.value)}
            value={title}
            placeholder={'Task name'}
          />
        </div>

        <div className={styles.CreateTaskItem}>
          <Textarea
            rows={5}
            placeholder='Describe your task'
            onChange={e => setDescr(e.target.value)}
            value={descr}
            onClick={() => createTask()}
            />
        </div>

        <div className={styles.CreateTaskItem}>
          <MyButton className={'green-btn'} onClick={() => createTask()}>Create Task</MyButton>
        </div>

      </div>
    </div>
  )
}
