import React from 'react'

import { MyButton } from '../UI/button/MyButton'

import styles from './Statuses.module.css'


const statusesArr = ['active', 'in progress', 'urgent', 'done']

export const Statuses = () => {

  const [status, setStatus] = React.useState(statusesArr)


  // console.log(status)

  const choiceStatus = (e, curStatus) => {
    setStatus(curStatus)
    console.log(e)
  }

  return (
    <div className={styles.statusesWrap}>
      {
        statusesArr
        .filter(status => status !== 'done')
        .map((status, index) => 
          <MyButton
            key={status + index}
            onClick={e => choiceStatus(e, status)} 
            className={`${status}-btn`}
          >
            {status}
          </MyButton>
        )
      }
    </div>
  )
}
