import React from 'react'

import { useDispatch, useSelector } from 'react-redux'

import { MyButton } from '../UI/button/MyButton'
import { MyInput } from '../UI/input/MyInput'

import { creaTask } from '../../redux/slices/tasksSlice'
import { setIsOpen } from '../../redux/slices/isOpenSlice'
import { Textarea } from '../UI/textarea'

export const CreateTask = () => {
  const [title, setTitle] = React.useState('')

  const dispatch = useDispatch()
  const tasks = useSelector(state => state.createTask.tasks)


  const createTask = () => {
    const newTask = {
      id: Date.now(),
      title,
    }

    dispatch(creaTask([...tasks, newTask]))
    dispatch(setIsOpen(false))
    setTitle('')

  }


  return (
    <div className={styles.CreateTaskWrap}>

      <MyInput
        onChange={e => setTitle(e.target.value)} 
        value={title} 
        placeholder={'Опишите задачу'} 
      />

      <Textarea></Textarea>

      <MyButton className={'green-btn'} onClick={() => createTask()}>Создать задачу</MyButton>

    </div>
  )
}
