import React from 'react'
import { useSelector } from 'react-redux'

import styles from './StatusStatistics.module.css'

export const StatusStatistics = () => {
  const statusCount = useSelector((state) => state.createTask.statusCount)
  const statusInfo = useSelector((state) => state.createTask.statusInfo)
  // statusCount['done']

  // StatusStatisticsItem
  return (
    <div className={styles.StatusStatisticsWrap}>
      {statusInfo.map((obg) => (
        <div className={styles.StatusStatisticsItem}>
          <img src={`./img${obg.urlImg}`} alt={obg.name} />
          <div>
            <span>{obg.name}</span>
            <span>{statusCount[obg.name]}</span>
          </div>
        </div>
      ))}

      <img src='' alt='' />
    </div>
  )
}
