import { createSlice } from '@reduxjs/toolkit'


const initialState = {
  tasks: [
    {
      id: 12,
      title: 'Driver monitor and method for monitoring driver',
      descr: 'fdgfgfg',
      selectedStatus: 'active',
    },
  ],

  // statusInfo: [
  //   {name: 'active'},
  //   {name: 'in progress'},
  //   {name: 'urgent'},
  //   {name: 'done'},
  // ],

  selectedStatus: '',

  statusCount: {
    'active': 1,
    'in progress': 0,
    'urgent': 0,
    'done': 0,
  },

}

export const tasksSlice = createSlice({
  name: 'tasks',
  initialState,
  reducers: {
    creaTask: (state, action) => {
      state.tasks = action.payload
    },

    setSelectedStatus: (state, action) => {
      state.selectedStatus = action.payload
    },

    updStatusCount: (state, action) => {
      state.statusCount = action.payload
    },

  },
})


export const { creaTask, setSelectedStatus, updStatusCount } = tasksSlice.actions

export default tasksSlice.reducer
