import React from 'react'

import { timeConverter } from '../../utils/timeConverter'

import styles from './TaskItem.module.css'

export const TaskItem = ({date, title, descr}) => {
  return (
    <div className={styles.task}>
      <h2>{title}</h2>
      <span>{timeConverter(date)}</span>
      <div>{descr}</div>
    </div>
  )
}
