import React from "react";
import { TaskItem } from "../TaskItem";
import { MyButton } from "../UI/button/MyButton";
import { MyInput } from "../UI/input/MyInput";

import { useDispatch, useSelector } from 'react-redux'
import { creaTask } from '../../redux/slices/tasksSlice'

export const CreateTask = () => {
  const [title, setTitle] = React.useState('')
  // const [tasks, setTasks] = React.useState([])

  const dispatch = useDispatch()
  const tasks = useSelector(state => state.createTask.tasks)


  const createTaskA = () => {
    const newTask = {
      id: Date.now(),
      title
    }

    dispatch(creaTask(newTask))
    // setTasks([...tasks, newTask]);
    setTitle('')

  }



  return (
    <div>

      <MyInput
        onChange={e => setTitle(e.target.value)} 
        value={title} 
        placeholder={'Опишите задачу'} 
      />

      {console.log(tasks)}

      <MyButton className={'green-btn'} onClick={() => createTaskA()}>Создать задачу</MyButton>

      {
        tasks.map(taskObj => (
          <TaskItem key={taskObj.id} date={taskObj.id} title={taskObj.title}/>
        ))
      }

    </div>
  );
}
